$(document).ready(function(){$(".toggle").each(function(){$(".toggle-panel",this).hide(),$(".minus",this).hide(),$(".toggle-head").click(function(){$(this).toggleClass("active");$(this).find(".plus").toggle(),$(this).find(".minus").toggle();$(this).next(".toggle-panel").toggleClass("active").slideToggle()})})});